import { useState, useEffect } from "react";

function App() {
    const [loading, setLoading] = useState(true);
    const [coins, setCoins] = useState([]);
    const [usd, setUsd] = useState(0);
    const [btc, setBtc] = useState(coins[0].quotes.USD.price);
    const onChange = (event) => (event.target.value);
    const onSubmit = (event) => {
        event.preventDefault();
        console.log(usd);
        if(usd === 0) {
            return;
        }
        setUsd((current) => usd/btc);
    }
    useEffect(() => {
        fetch("https://api.coinpaprika.com/v1/tickers")
            .then((response) => response.json())
            .then((json) => {
                setCoins(json);
                setLoading(false);
            }); 
    }, [])
    return (<div>
        <h1>The Coins! ({coins.length})</h1>
        {loading ? <strong>Loading...</strong> :  null}
        <form onSubmit={onSubmit}>
            <input onChange={onChange} value={usd} type="number" placeholder="Input USD" />
        </form>
        <ul>
            {coins.map((coin) => <li>{coin.name} ({coin.symbol}): ${coin.quotes.USD.price} </li>)}
        </ul>
    </div>);
}

export default App;
