import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Point from "../components/Point";
import Loading from "../components/Loading";
import styles from "./Home.module.css";

function Detail() {
    const [loading, setLoading] = useState(true);
    const [movieDetail, setMovie] = useState();
    const {id} = useParams();
    const getMovie = async () => {
        const json = await(
            await fetch(`https://yts.mx/api/v2/movie_details.json?movie_id=${id}`))
            .json();
        console.log(json);
        setMovie(json.data);
        setLoading(false);
        console.log(json.data.movie);
    };
    useEffect(() => {
        getMovie();
    }, []);
    return (
        <div className={styles.container}>
            {loading ? <Loading />:
                <div>
                    <Point
                        key={movieDetail.movie.id}
                        id={movieDetail.movie.id}
                        background_image_original={movieDetail.background_image_original}
                        medium_cover_image={movieDetail.medium_cover_image}
                        url={movieDetail.url}
                        title_long={movieDetail.title_long}
                        rating={movieDetail.movie.rating}
                        coverImg={movieDetail.movie.large_cover_image}
                        title={movieDetail.movie.title}
                        description_full={movieDetail.movie.description_full}
                        runtime={movieDetail.movie.runtime} 
                        genres={movieDetail.movie.genres}

                    />
                </div>
            }
        </div>
    );
}

export default Detail;
